#include <stdio.h>
#include <time.h>
#include <stdlib.h>
#include <math.h>

char * longestPalindrome(char * s){
    int start = 0;
    int length = 0;
    for (int i = 0; s[i] != '\0'; i++) {
        for (int j = i; s[j] != '\0'; j++) {
            if (s[i] == s[j]) {
                int front = i;
                int rear = j;
                int flag = 1;
                while (front < rear) {
                    if (s[front] != s[rear]) {
                        flag = 0;
                        break;
                    }
                    front++;
                    rear--;
                }
                if (flag) {
                    int temp = j - i + 1;
                    if (length < temp) {
                        length = temp;
                        start = i;
                    }
                }
            }
        }
    }

    s[start+length-1] = '\0';
    return s+start;
}

int main()
{
    char s[1000] = "";
    char* str = longestPalindrome(s);
    printf("%s", str);
}
